import React, { useState } from 'react'
import '../../scss/main.scss'
import Duck1 from '../../images/duck1.png'
import { useNavigate } from 'react-router-dom'
import { changeCharacter } from '../../slices/character'
import { useDispatch } from 'react-redux'
import Button from '../../components/Buttons'

interface CharacterProps {
  image: any
  alt: string
  selectedImage: () => void
  dataCy?: string
  errorMessage?: () => void
}
interface CharacterState {
  selectedImageMessage: boolean
  errorMessage: boolean
}

const CharacterImages = ({
  image,
  alt,
  selectedImage,
  dataCy,
  errorMessage,
}: CharacterProps) => {
  const dispatch = useDispatch()

  const selectCharacterHandler = (selectedCharacter: any) => {
    if (selectedCharacter) {
      dispatch(changeCharacter(selectedCharacter))
      localStorage.setItem('character-image', selectedCharacter)
      selectedImage()
    } else {
      errorMessage && errorMessage()
    }
  }

  return (
    <div className='image-container'>
      <img
        src={image}
        alt={alt}
        className='duck-image'
        onClick={() => selectCharacterHandler(image)}
        data-cy={dataCy}
      />
    </div>
  )
}

const Character = () => {
  const [state, setState] = useState<CharacterState>({
    selectedImageMessage: false,
    errorMessage: false,
  })
  // const [selectedImageMessage, setSelectedImageMessage] =
  //   useState<boolean>(false)
  // const [errorMessage, setErrorMessage] = useState<boolean>(false)

  const navigate = useNavigate()
  const navigateToLoginPage = () => {
    if (state.selectedImageMessage) {
      navigate('/login')
    } else {
      setState((prev) => ({ ...prev, errorMessage: true }))
      // setErrorMessage(true)
    }
  }
  const navigateToHomepage = () => navigate('/')

  return (
    <div className='character-container'>
      <h1 className='character-heading' data-cy='character-heading'>
        Choose Your Duck
      </h1>
      <CharacterImages
        image={Duck1}
        alt='duck1'
        selectedImage={() =>
          setState((prev) => ({ ...prev, selectedImageMessage: true }))
        }
        errorMessage={() =>
          setState((prev) => ({ ...prev, errorMessage: true }))
        }
        dataCy='duck-image1'
      />
      {state.selectedImageMessage && (
        <p
          className='character-chosen-message'
          data-cy='character-chosen-message'
        >
          Your character has been chosen
        </p>
      )}
      {state.errorMessage && (
        <p className='character-chosen-message'>Please Select your character</p>
      )}
      <div className='button-container'>
        <Button
          title='Back'
          onClick={navigateToHomepage}
          dataCy='back-button'
        />
        <Button
          title='Next'
          onClick={navigateToLoginPage}
          dataCy='next-button'
        />
      </div>
    </div>
  )
}

export default Character
