import React, { useState, useEffect } from 'react'
import Button from '../../components/Buttons'
import { useSelector } from 'react-redux'
import { socket } from '../../socket/index'

const Lobby = () => {
  const [message, setMessage] = useState('')
  const [messages, setMessages] = useState([])

  const selectedRoom = useSelector((state: any) => state.playerSlice.gamingRoom)
  const selectedNickname = useSelector(
    (state: any) => state.playerSlice.nickname
  )

  console.log('im here', selectedNickname)

  useEffect(() => {
    socket.on('receive-message', (newMessage) => {
      setMessages(newMessage) // This line replaces the entire array
    })
  }, [])
  const submitMessageHandler = () => {
    if (!message) {
      setMessage('Please enter your message')
      return
    } else {
      socket.emit('send-message', message)
    }
    setMessage('')
  }

  return (
    <div>
      <h1>Lobby</h1>
      <div className='lobby-container'></div>
      <p>{selectedNickname}</p>
      {messages.map((message, index) => (
        <div key={index}>{message}</div>
      ))}
      <input onChange={(e) => setMessage(e.target.value)} value={message} />
      <Button title='Send Message' onClick={submitMessageHandler} />
    </div>
  )
}

export default Lobby
