import React, { useRef, useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { changeCharacter } from '../../slices/character'
import { gamingRoom, nickname } from '../../slices/player'
import '../../scss/main.scss'
import Button from '../../components/Buttons'
import { useNavigate } from 'react-router-dom'
import { socket } from '../../socket/index'

// this the socket client version

// use redux to use the nickname and room
const Login = () => {
  const inputNicknameRef = useRef<HTMLInputElement>(null)
  const inputRoomRef = useRef<HTMLInputElement>(null)
  // const [room, setRoom] = useState('') // redux
  // const [nickName, setNickname] = useState('') // redux
  const [message, setMessage] = useState('')
  const dispatch = useDispatch()
  const selectedCharacter = useSelector(
    (state: any) => state.characterSlice.character
  )
  const selectedRoom = useSelector((state: any) => state.playerSlice.gamingRoom)
  const selectedNickname = useSelector(
    (state: any) => state.playerSlice.nickname
  )
  const navigate = useNavigate()

  // socket.on('connect', () => {
  //   setMessage(`You connected with the id, ${socket.id}`)
  // })

  // socket.on('join-room', (name) => {
  //   setMessage(`You have joined the room ${name}`)
  // })
  // listens the event and displays the message

  // how to send from the client to the server
  // socket.emit('custom event', 10, 'hi', { a: 1 })

  const joinRoomHandler = async () => {
    if (!selectedNickname || !selectedRoom) {
      setMessage(
        'Please enter your nickname and the existing room to join the game'
      )
      return
    }

    try {
      const room = await new Promise((resolve, reject) => {
        socket.emit(
          'create-room',
          selectedRoom,
          selectedNickname,
          (room: boolean) => {
            resolve(room)
          }
        )
      })

      if (room) {
        navigate('/lobby')
      } else {
        setMessage('The room does not exist')
      }
    } catch (error) {
      console.error('Error creating room:', error)
      setMessage('An error occurred while creating the room')
    }
  }
  const joinNewGameHandler = () => {
    if (!selectedNickname || !selectedRoom) {
      setMessage('Please enter your username and room to create a quiz')
      return
    } else {
      socket.emit(
        'create-room',
        selectedRoom,
        selectedNickname,
        (room: string) => {
          setMessage(`Room ${room} has been created`)
          dispatch(gamingRoom(room))
        }
      )
    }
    dispatch(gamingRoom(''))
    dispatch(nickname(''))
  }
  useEffect(() => {
    if (inputNicknameRef.current) {
      inputNicknameRef.current.focus()
    }
    if (inputRoomRef.current) {
      inputRoomRef.current.focus()
    }
    const storedCharacter = localStorage.getItem('character-image')
    if (storedCharacter) {
      dispatch(changeCharacter(storedCharacter))
    }
  }, [dispatch, selectedCharacter])

  return (
    <div className='login-container'>
      {selectedCharacter && (
        <img
          src={selectedCharacter}
          alt='selected-character'
          className='selected-character'
          data-cy='selected-character'
        />
      )}
      <p className='nickname' data-cy='nickname'>
        Username
      </p>
      <input
        type='text'
        ref={inputNicknameRef}
        className='input'
        data-cy='input'
        onChange={(e) => dispatch(nickname(e.target.value))}
        value={selectedNickname}
      />
      <p className='nickname' data-cy='nickname'>
        Room
      </p>
      <input
        type='text'
        ref={inputRoomRef}
        className='input'
        data-cy='input'
        onChange={(e) => dispatch(gamingRoom(e.target.value))}
        value={selectedRoom}
      />

      <div className='start-game-container'>
        <Button title='Create Room' onClick={joinNewGameHandler} />
        <Button
          title='Join Game'
          onClick={joinRoomHandler}
          dataCy='join-button'
        />
      </div>

      {message && <p className='not-entered-message'>{message}</p>}
    </div>
  )
}

export default Login
