import React, { useState, useEffect } from 'react'
import Button from '../../components/Buttons'
import { useSelector } from 'react-redux'
import { socket } from '../../socket/index'
import { useDispatch } from 'react-redux'
import { nickname } from '../../slices/player'

const Lobby = () => {
  const [message, setMessage] = useState('')
  const [messages, setMessages] = useState<string[]>([])

  const selectedNickname = useSelector(
    (state: any) => state.playerSlice.nickname
  )
  const selectedRoom = useSelector((state: any) => state.playerSlice.gamingRoom)
  const dispatch = useDispatch()

  useEffect(() => {
    socket.on('update-nicknames', (updatedNicknames: string[]) => {
      dispatch(nickname(updatedNicknames))
    })

    return () => {
      socket.off('update-nicknames')
    }
  }, [dispatch])

  socket.on('receive-message', (newMessage: string[]) => {
    // Update messages state properly by either spreading the existing messages array
    // or concatenating the new message with existing ones.
    setMessages(newMessage)
    console.log('New message received:', newMessage) // Check if the new message is received correctly
  })

  const submitMessageHandler = () => {
    if (!message) {
      setMessage('Please enter your message')
      return
    } else {
      // Emitting the message along with the room information
      socket.emit('send-message', message, selectedRoom)
    }
    setMessage('')
  }

  return (
    <div>
      <h1>Lobby</h1>
      <div className='lobby-container'>
        <p style={{ fontSize: '4rem' }}>
          {selectedNickname.length > 0 &&
            selectedNickname.map((nickname) => <p>{nickname}</p>)}
          :
        </p>
        {messages.map((message, index) => (
          <p key={index} style={{ color: 'white' }}>
            {message}
          </p>
        ))}
      </div>
      <input onChange={(e) => setMessage(e.target.value)} value={message} />
      <Button title='Send Message' onClick={submitMessageHandler} />
    </div>
  )
}

export default Lobby
