import React, { useState } from 'react'
import '../../scss/main.scss'
import Duck1 from '../../images/duck1.png'
import { useNavigate } from 'react-router-dom'
import { changeCharacter } from '../../slices/character'
import { useDispatch } from 'react-redux'
import Button from '../../components/Buttons'

interface CharacterProps {
  image: any
  alt: string
  selectedImage: () => void
  dataCy: string
}

const CharacterImages = ({
  image,
  alt,
  selectedImage,
  dataCy,
}: CharacterProps) => {
  const dispatch = useDispatch()

  const selectCharacterHandler = (selectedCharacter: any) => {
    if (selectedCharacter) {
      dispatch(changeCharacter(selectedCharacter))
      localStorage.setItem('character-image', selectedCharacter)
      selectedImage()
    }
  }

  return (
    <div className='image-container'>
      <img
        src={image}
        alt={alt}
        className='duck-image'
        onClick={() => selectCharacterHandler(image)}
        data-cy={dataCy}
      />
    </div>
  )
}

const Character = () => {
  const [selectedImageMessage, setSelectedImageMessage] = useState(false)

  const navigate = useNavigate()
  const navigateToLoginPage = () => navigate('/login')
  const navigateToHomepage = () => navigate('/')

  return (
    <div className='character-container'>
      <h1 className='character-heading' data-cy='character-heading'>
        Choose Your Duck
      </h1>
      <CharacterImages
        image={Duck1}
        alt='duck1'
        selectedImage={() => setSelectedImageMessage(true)}
        dataCy='duck-image1'
      />
      {selectedImageMessage && (
        <p className='character-chosen-message'>
          Your character has been chosen
        </p>
      )}
      <div className='button-container'>
        <Button title='Back' onClick={navigateToHomepage} />
        <Button title='Next' onClick={navigateToLoginPage} />
      </div>
    </div>
  )
}

export default Character
