import React, { useRef, useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { changeCharacter } from '../../slices/character'
import '../../scss/main.scss'
import Button from '../../components/Buttons'
import { useNavigate } from 'react-router-dom'
import { io } from 'socket.io-client'
// this the socket client version

const Login = () => {
  const inputRef = useRef<HTMLInputElement>(null)
  const [nickname, setNickName] = useState('')
  const dispatch = useDispatch()
  const selectedCharacter = useSelector(
    (state: any) => state.characterSlice.character
  )
  const navigate = useNavigate()

  const navigateToTheCharacterPage = () => navigate('/character')
  const navigateToTheRulePage = () => navigate('/')

  const socket = io('http://localhost:5001/')

  useEffect(() => {
    if (inputRef.current) {
      inputRef.current.focus()
    }
    const storedCharacter = localStorage.getItem('character-image')
    if (storedCharacter) {
      dispatch(changeCharacter(storedCharacter))
    }
  }, [dispatch, selectedCharacter])

  return (
    <div className='login-container'>
      {selectedCharacter && (
        <img
          src={selectedCharacter}
          alt='selected-character'
          className='selected-character'
          data-cy='selected-character'
        />
      )}
      <p className='nickname' data-cy='nickname'>
        Nickname
      </p>
      <input
        type='text'
        ref={inputRef}
        className='input'
        data-cy='input'
        onChange={(e) => setNickName(e.target.value)}
      />

      <div className='button-container'>
        <Button
          title='Back'
          onClick={navigateToTheCharacterPage}
          dataCy='back-button'
        />
        <Button title='Next' />
      </div>
    </div>
  )
}

export default Login
