import React, { useState, useEffect } from 'react'
import Button from '../../components/Buttons'
import { useSelector } from 'react-redux'
import { socket } from '../../socket/index'
import { useDispatch } from 'react-redux'
import { nickname } from '../../slices/player'

const Lobby = () => {
  const [message, setMessage] = useState('')
  const [messages, setMessages] = useState<
    { nickname: string; message: string }[]
  >([])

  const selectedNickname = useSelector(
    (state: any) => state.playerSlice.nickname
  )

  const selectedRoom = useSelector((state: any) => state.playerSlice.gamingRoom)
  const dispatch = useDispatch()

  useEffect(() => {
    socket.on('update-nicknames', (updatedNicknames: string[]) => {
      dispatch(nickname(updatedNicknames))
    })
    socket.on(
      'receive-message',
      ({
        nickname: senderNickname,
        message,
      }: {
        nickname: string
        message: string
      }) => {
        console.log('Received message:', { senderNickname, message }) // Add logging here
        setMessages((prevMessages) => [
          ...prevMessages,
          { nickname: senderNickname, message },
        ])
      }
    )
  }, [])
  const submitMessageHandler = () => {
    if (!message) {
      setMessage('Please enter your message')
      return
    }
    socket.emit('send-message', message, selectedRoom)
    setMessage('')
  }

  return (
    <div>
      <h1>Lobby</h1>
      <div className='lobby-container'>
        {messages.map(({ nickname, message }, index) => (
          <p key={index} style={{ fontSize: '4rem', color: 'white' }}>
            {nickname}: {message}
          </p>
        ))}
      </div>
      <input onChange={(e) => setMessage(e.target.value)} value={message} />
      <Button title='Send Message' onClick={submitMessageHandler} />
    </div>
  )
}

export default Lobby
