import React, { useState, useEffect, useRef, useReducer } from 'react'
import Button from '../../components/Buttons'
import { useSelector } from 'react-redux'
import { socket } from '../../socket/index'
import { useDispatch } from 'react-redux'
import { nickname } from '../../slices/player'
import Logo from '../../components/Logo'
import MessageLogo from '../../images/send-message-icon.png'

type LobbyState = {
  message: string
  messages: Array<string>
}

const Lobby = () => {
  const initialState: LobbyState = {
    message: '',
    messages: [],
  }
  const [message, setMessage] = useState('')
  const [errorMessage, setErrorMessage] = useState('')
  const [messages, setMessages] = useState<
    { nickname: string; message: string }[]
  >([])

  const selectedRoom = useSelector((state: any) => state.playerSlice.gamingRoom)
  const selectedNickname = useSelector(
    (state: any) => state.playerSlice.nickname
  )
  const selectedCharacter = useSelector(
    (state: any) => state.characterSlice.character
  )
  const dispatch = useDispatch()

  useEffect(() => {
    socket.on('update-nicknames', (updatedNicknames: string[]) => {
      dispatch(nickname(updatedNicknames))
    })

    const receiveMessageHandler = ({
      nickname: senderNickname,
      message,
    }: {
      nickname: string
      message: string
    }) => {
      setMessages((prevMessages) => [
        ...prevMessages,
        { nickname: senderNickname, message },
      ])
    }

    socket.on('receive-message', receiveMessageHandler)

    // Cleanup function repeated messages
    return () => {
      socket.off('update-nicknames')
      socket.off('receive-message', receiveMessageHandler)
    }
  }, [])

  const submitMessageHandler = () => {
    if (!message) {
      setErrorMessage('Please enter your message')
      return
    }
    socket.emit('send-message', message, selectedRoom)
    setMessage('')
  }

  return (
    <div className='lobby-container'>
      <Logo />
      <h1 className='lobby-title'>Lobby</h1>
      <p className='lobby-waiting-sentence'>
        Waiting for the host to start the game....
      </p>
      <div className='logo-message-container'>
        <div className='lobby-message-box'>
          {selectedCharacter}
          {messages.map(({ nickname, message }, index) => (
            <p key={index} className='chat-message'>
              {nickname}: {message}
            </p>
          ))}
        </div>
        <div className='lobby-input-message-container'>
          <input
            onChange={(e) => setMessage(e.target.value)}
            value={message}
            className='lobby-input'
            placeholder='Enter your message'
          />
          <img
            src={MessageLogo}
            alt='message-logo'
            className='message-logo'
            onClick={submitMessageHandler}
          />
        </div>
        {selectedNickname.map((nickname: Array<string>, index: number) => (
          <p key={index} className='nicknames'>
            {nickname}
          </p>
        ))}
      </div>
      <Button title='Start The Quiz' />
      <p className='lobby-message'>{errorMessage && errorMessage}</p>
    </div>
  )
}

export default Lobby
