import React, { useState, useEffect } from 'react'
import Button from '../../components/Buttons'
import { useSelector } from 'react-redux'
import { socket } from '../../socket/index'

const Lobby = () => {
  const [message, setMessage] = useState('')
  const [messages, setMessages] = useState([])

  const selectedNickname = useSelector(
    (state: any) => state.playerSlice.nickname
  )
  const selectedRoom = useSelector((state: any) => state.playerSlice.gamingRoom)

  useEffect(() => {
    socket.on(
      'receive-message',
      (newMessage) => {
        // Update messages state properly by either spreading the existing messages array
        // or concatenating the new message with existing ones.
        setMessages((prevMessages) => [...prevMessages, newMessage])
      },
      []
    )

    // Clean up socket event listener on component unmount to prevent memory leaks
    return () => {
      socket.off('receive-message')
    }
  }, []) // Empty dependency array ensures the effect runs only once on mount

  const submitMessageHandler = () => {
    if (!message) {
      setMessage('Please enter your message')
      return
    } else {
      // Emitting the message along with the room information
      socket.emit('send-message', message, selectedRoom)
    }
    setMessage('')
  }

  return (
    <div>
      <h1>Lobby</h1>
      <div className='lobby-container'>
        <p>{selectedNickname && selectedNickname}</p>
        {messages.map((message, index) => (
          <div key={index}>{message}</div>
        ))}
      </div>
      <input onChange={(e) => setMessage(e.target.value)} value={message} />
      <Button title='Send Message' onClick={submitMessageHandler} />
    </div>
  )
}

export default Lobby
